import React, { Component } from 'react'
import NewsItems from './NewsItems'
import Spinner from './Spinner';
import PropTypes from 'prop-types'


export class NewsShow extends Component {
  static defaultProps ={
    country: "in",
    category:"general",
    pageSize:6

  }
  static propTypes={
    country:PropTypes.string,
    pageSize:PropTypes.number,
    category:PropTypes.string,

  }

  articles =[]
  loading=false;
    constructor(){
        super();
        this.state={
        articles:this.articles,
        loading:true,
        page:1
      }
    }
    async componentDidMount(){
        let url =`https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=19482a63981f4fe786ff71d0c95930bc&page=2&pageSize=${this.props.pageSize}`;
        let data =await fetch(url);
        this.setState({loading:true})
        let parsedData =await data.json();
        console.log(parsedData)
        this.setState({
          articles:parsedData.articles,
          totalResults:parsedData.totalResults,
          loading:false

        })
    }
    
    hadlePrevClick= async()=>{
      let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=19482a63981f4fe786ff71d0c95930bc&page=${this.state.page - 1}&pageSize=${this.props.pageSize}`
      let data =await fetch(url);
      let parsedData =await data.json();
      this.setState({loading:true})
      this.setState({
          page:this.state.page - 1,
          articles:parsedData.articles,
          loading:false
       })
  }
  hadleNextClick = async()=>{
   
         let url =`https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=19482a63981f4fe786ff71d0c95930bc&page=${this.state.page + 1}&pageSize=${this.props.pageSize}`
         let data =await fetch(url);
         this.setState({loading:true})
         let parsedData =await data.json();
         this.setState({
              page:this.state.page + 1,
             articles:parsedData.articles,
             loading:false
     })
    }
  render() {
          return(
          <div className='container my-3'>
           <h1 className='text-center '> <b>News 24/7 -- Top Headline</b></h1>

          {this.state.loading &&<Spinner/>}            
            <div className="row">
           {!this.state.articles.map((element)=>{
               return <div className='col-md-4' key={element.url} >
               <NewsItems title={element.title} description={element.description} publishedAt={element.publishedAt} urlToImage={element.urlToImage} url={element.url} />
               </div>
            } )}
      </div>
      <div className="container d-flex justify-content-between">
      <button disabled={this.state.page<=1} type="button" className="btn btn-dark" onClick={this.hadlePrevClick} >Previous</button>
      <button disabled={(this.state.page + 1>(Math.ceil (this.state.totalResults/this.props.pageSize)))} type="button" className="btn btn-dark " onClick={this.hadleNextClick} >Next</button>
      </div>
       </div>
          )
   }
}


export default NewsShow
