import React, { Component } from 'react'
import NewsItems from './NewsItems'

export class NewsShow extends Component {

  articles =[]
  loading=false;
  page =1;
    constructor(){
        super();
        this.state={
        articles:this.articles,
        loading:false,
      }
    }
    async componentDidMount(){
        let url ="https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=19482a63981f4fe786ff71d0c95930bc&page=1&pagesize=6";
        let data =await fetch(url);
        let parsedData =await data.json();
        console.log(parsedData)
        this.setState({articles:parsedData.articles,totalResults:parsedData.totalResults})
    }
    
    hadlePrevClick= async()=>{
      let url =`https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=19482a63981f4fe786ff71d0c95930bc&page=${this.state.page - 1}&pagesize=6`
      let data =await fetch(url);
      let parsedData =await data.json();
      console.log(parsedData)
      this.setState({
          page:this.state.page - 1,
          articles:parsedData.articles,
       })
  }
  hadleNextClick = async()=>{
    if(this.state.page + 1>(Math.ceil (this.state.totalResults/6))){

    }
    else{
         let url =`https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=19482a63981f4fe786ff71d0c95930bc&page=${this.state.page + 1}&pageSize=6`
         let data =await fetch(url);
         let parsedData =await data.json();
         console.log(parsedData)
         this.setState({
              page:this.state.page + 1,
             articles:parsedData.articles,
     })
    }
}
  render() {
    return (
          
          <div className='container my-3'>
           <h2 >Top Headline</h2>
            <div className="row">
           {this.state.articles.map((element)=>{
               return <div className='col-md-4' key={element.url} >
               <NewsItems title={element.title.slice(0,60)} description={element.description.slice(0,80)} urlToImage={element.urlToImage} url={element.url} />
               </div>
            } )}
      </div>
      <div className="container d-flex justify-content-between">
      <button disabled={this.state.page<=1} type="button" className="btn btn-dark" onClick={this.hadlePrevClick} >Previous</button>
      <button  type="button" className="btn btn-dark " onClick={this.hadleNextClick} >Next</button>
      </div>
       </div>
   )
   }
}


export default NewsShow
