import React, { Component } from 'react'

export class DarkMode extends Component {
    
    static defaultProps ={
        darkMode:"dark",
        lightMode:"light"
    
      }
      static propTypes={
        darkMode:PropTypes.string,
        lightMode:PropTypes.string,
      }
    toggleMode = async()=>{
        if(darkMode === "dark"){
           this.setState({darkMode: "light"})
           this.setState({lightMode:"dark"})
        }
        else{
          this.setState({darkMode:"dark"})
          this.setState({lightMode:"light"})
    
        }
      }
  render() {
    return (
      <div>
        <div class="form-check form-switch d-flex justify-content-end">
                  <input onClick={this.toggleMode} className="form-check-input" type="checkbox" role="switch" id="flexSwitchCheckDefault"/>
                  <label className="form-check-label text-light" for="flexSwitchCheckDefault">Dark</label>
        </div>
      </div>
    )
  }
}

export default DarkMode
