import React, { Component } from 'react'
import NewsItems from './NewsItems'

export class NewsShow extends Component {
    
    
    constructor(){
        super();
        this.state={
        articles:this.articles,
        loading:false,
        page:1
    }
    }
    async componentDidMount(){
        let url ="https://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=c51edf4abfe14356ab1512dc1e5b7854&page=1"
        let data =await fetch(url);
        let parseData =await data.json();
        console.log(parseData)
        this.setState({
           articles:parseData.articles
        })
    }
    
  render() {
    return (
        
      <div className='container my-3'>
      <h2 className='container text-center' >Top Headline</h2>
     
      <div className='row'>
      {this.state.articles.map((element)=>{
        return <div className='col-md-4' key={element.url} >
               <NewsItems title={element.title} description={element.description} imgUrl={element.imgUrl} newsUrl={element.newsUrl} />
               </div>
        
            }
      )}
      


      </div>
      </div>
      
    )
  }
}

export default NewsShow
