import React, { Component } from 'react'
import './App.css';
import Navbars from './Componets/Navbars';
import NewsShow from './Componets/NewsShow';
import {
  BrowserRouter as Router,
  Switch,
  Route,


} from "react-router-dom";
export class App extends Component {
  pageSize =5;
  render() {
    return (
      <div>
      <Router>
      <Navbars/>
      <div class="form-check form-switch">
  <input className="form-check-input" type="checkbox" role="switch" id="flexSwitchCheckChecked" checked/>
  <label className="form-check-label" for="flexSwitchCheckChecked">Checked switch checkbox input</label>
</div>
      <Switch>
      <Route exact path="/general"><NewsShow key='home' pageSize={this.pageSize} country='in' category='general' /> </Route>
          <Route exact path="/business"><NewsShow key="business" pageSize={6} country='in' category='business'/> </Route>
          <Route exact path="/entertainment"><NewsShow key='entertainment'pageSize={6} country='in' category='entertainment'/> </Route>
          <Route exact path="/general"><NewsShow key='general'pageSize={6} country='in' category='general'/> </Route>
          <Route exact path="/health"><NewsShow key='health'pageSize={6} country='in' category='health'/> </Route>
          <Route exact path="/science"><NewsShow key='science'pageSize={6} country='in' category='science'/> </Route>
          <Route exact path="/sports"><NewsShow key='sports'pageSize={6} country='in' category='sports'/> </Route>
          <Route exact path="/technology"><NewsShow key='technology'pageSize={6} country='in' category='technology'/> </Route>

      </Switch>
      </Router>

      </div>
    )
    }
}

export default App
