import React, { Component } from 'react'

export class NewsItems extends Component {
  render() {
    let myStyle2 ={backgroundColor:'black'}
    let textWhite ={color:'white'}
    let { title, description, imgurl, newsUrl} = this.props;
    return (
      <div className="container my-3" style={textWhite} >
        <div className="card ">
          <img src={imgurl} className="card-img-top" alt="" />
          <div className="card-body" style={myStyle2} >
            <h5 className="card-title" style={textWhite}>{title}</h5>
            <p className="card-text">{description}</p>
            <a href={newsUrl} target="blank" className="btn btn-sm btn-primary btn-darknpmm">
              Read More...
            </a>
          </div>
        </div>
      </div>
    );
  }
}

export default NewsItems
