import React, { Component } from "react"
import "./App.css";
import Navbars from "./Componets/Navbars";
import NewsShow from "./Componets/NewsShow";
import {
  BrowserRouter as Router,
  Switch,
  Route,


} from "react-router-dom";
export class App extends Component {
  pageSize=8;
  render() {
    return (
      <div>
      <Router>
      <Navbars/>
      <Switch>
          <Route exact path="/"><NewsShow key="general" pageSize={this.pageSize} country="in" category="general" /> </Route>
          <Route exact path="/business"><NewsShow key="business" pageSize={this.pageSize} country="in" category="business"/> </Route>
          <Route exact path="/entertainment"><NewsShow key="entertainment"pageSize={this.pageSize} country="in" category="entertainment"/> </Route>
          <Route exact path="/general"><NewsShow key="general"pageSize={this.pageSize} country="in" category="general"/> </Route>
          <Route exact path="/health"><NewsShow key="health"pageSize={this.pageSize} country="in" category="health"/> </Route>
          <Route exact path="/science"><NewsShow key="science"pageSize={this.pageSize} country="in" category="science"/> </Route>
          <Route exact path="/sports"><NewsShow key="sports"pageSize={this.pageSize} country="in" category="sports"/> </Route>
          <Route exact path="/technology"><NewsShow key="technology"pageSize={this.pageSize} country="in" category="technology"/> </Route>

      </Switch>
      </Router>

      </div>
    )
    }
}

export default App
