import React, { Component } from 'react'

export class NewsItems extends Component {
  
  render() {
    let {title, description,urlToImage,url,publishedAt,source}=this.props;
    
    return (
      
      <div className={`my-3 border ${this.props.darkMode1}`}>
      
        <div className={`card bg-${this.props.darkMode1}`}>
        <span class="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-danger" style={{left:'90%',  zIntex:'1',translate:"(-100%,-8%)"}}>{source}</span>
           <img src={urlToImage} className="card-img-top" alt=""/>
            
         <div className="card-body">
         transform: translate(-100%,-8%)
         <h5 className={`card-title text-${this.props.lightMode1}`}>{title} </h5>
         <p className={`card-text text-${this.props.lightMode1}`}>{description}</p>
         <p className="card-text">{publishedAt}</p>
         
         <a rel="noreferrer" href={url} target='_blank' className="btn btn-primary">Read Mode</a>
       </div>
       </div>
      </div>
    )
  }
}

export default NewsItems
