import React, { Component } from 'react'
import NewsItems from './NewsItems'
import Spinner from './Spinner';
import PropTypes from 'prop-types'


export class NewsShow extends Component {
  static defaultProps = {
      country: 'in',
      pageSize: 8, 
      category: 'general',
    }

    static propTypes = {
      country: PropTypes.string,
      pageSize: PropTypes.number, 
      category: PropTypes.string,
    }

  constructor(){
      super();H
      this.state = {
          articles: [],
          loading: false,
          page:1
      }
  }

  async componentDidMount(){ 
      let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=dbe57b028aeb41e285a226a94865f7a7&page=1&pageSize=${this.props.pageSize}`;
      this.setState({loading: true});
      let data = await fetch(url);
      let parsedData = await data.json()
      console.log(parsedData); 
      this.setState({articles: parsedData.articles,
          totalResults: parsedData.totalResults,
          loading: false})
  }

   handlePrevClick = async ()=>{
      console.log("Previous");
      let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=dbe57b028aeb41e285a226a94865f7a7&page=${this.state.page - 1}&pageSize=${this.props.pageSize}`;
      this.setState({loading: true});
      let data = await fetch(url);
      let parsedData = await data.json()
      console.log(parsedData);  
      this.setState({
          page: this.state.page - 1,
          articles: parsedData.articles,
          loading: false
      })

  }
  
   handleNextClick = async ()=>{
      console.log("Next"); 
      if (!(this.state.page + 1 > Math.ceil(this.state.totalResults/this.props.pageSize))){
          let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=dbe57b028aeb41e285a226a94865f7a7&page=${this.state.page + 1}&pageSize=${this.props.pageSize}`;
          this.setState({loading: true});
          let data = await fetch(url);
          let parsedData = await data.json() 
          this.setState({
              page: this.state.page + 1,
              articles: parsedData.articles,
              loading: false
          })
  }
      }

  render() { 
     let whiteText={
          color:'white',
      }
      return (
          <div className="container my-3">
              <h1 className="text-center" >NewsMonkey - Top Headlines</h1>
              {this.state.loading && <Spinner2/>}
              <div className="row"> 
              {!this.state.loading && this.state.articles.map((element)=>{
                  return <div className="col-md-4" key={element.url}>
                  
                  </div> 
              })} 
              <NewsItems/>
              <NewsItems title={element.title?element.title:""} description={element.description}/>

              </div> 
              <div className="container d-flex justify-content-between">
              <button disabled={this.state.page<=1} type="button" className="btn btn-dark" onClick={this.handlePrevClick}> &larr; Previous</button>
              <button disabled={this.state.page + 1 > Math.ceil(this.state.totalResults/this.props.pageSize)} type="button" className="btn btn-dark" onClick={this.handleNextClick}>Next &rarr;</button>
              </div>
          </div>
      )
  }
}

export default NewsShow
