import React, { Component } from 'react'
import './App.css';
import Navbars from './Componets/Navbars';
import NewsShow from './Componets/NewsShow';
import {
  BrowserRouter as Router,
  Switch,


} from "react-router-dom";
export class App extends Component {
  pageSize =5;

  toggleMode = async()=>{
    if(darkMode === "dark"){
       this.setState({darkMode: "light"})
       this.setState({lightMode:"dark"})
    }
    else{
      this.setState({darkMode:"dark"})
      this.setState({lightMode:"light"})

    }
  }

  render() {

    return (
      <div className={`bg-${this.props.darkMode}`}>
      <Router>
      <Navbars/>

      <Switch>
          <li exact path="/general"><NewsShow toggleMode={this.toggleMode} key='general' pageSize={this.pageSize} country='in' category='general' /> </li>
          <li exact path="/business"><NewsShow toggleMode={this.toggleMode} key="business" pageSize={6} country='in' category='business'/> </li>
          <li exact path="/entertainment"><NewsShow toggleMode={this.toggleMode} key='entertainment'pageSize={6} country='in' category='entertainment'/> </li>
          <li exact path="/general"><NewsShow toggleMode={this.toggleMode} key='general'pageSize={6} country='in' category='general'/> </li>
          <li exact path="/health"><NewsShow toggleMode={this.toggleMode} key='health'pageSize={6} country='in' category='health'/> </li>
          <li exact path="/science"><NewsShow toggleMode={this.toggleMode} key='science'pageSize={6} country='in' category='science'/> </li>
          <li exact path="/sports"><NewsShow toggleMode={this.toggleMode} key='sports'pageSize={6} country='in' category='sports'/> </li>
          <li exact path="/technology"><NewsShow  key='technology'pageSize={6} country='in' category='technology'/> </li>
      </Switch>
      </Router>

      </div>
    )
    }
}

export default App
