import React, { Component } from 'react'
import './App.css';
import Navbars from './Componets/Navbars';
import NewsShow from './Componets/NewsShow';
import {
  BrowserRouter as Router,
  Switch,
} from "react-router-dom";
export class App extends Component {
  constructor(props) {
    super(props);
    this.state = {

    };
  }
  pageSize =5;
  toggleMode = () => {
    this.setState({ darkMode: this.state.dark});
  }
  render() {
    let {dark}=this.props;
    return (
      <div className={`bg-${this.props.darkMode}`}>
      <Router>
      <Navbars/>
      <Switch>
          <li exact path="/general"><NewsShow Mode={dark} key='general' pageSize={this.pageSize} country='in' category='general' /> </li>
          <li exact path="/business"><NewsShow Mode={dark} key="business" pageSize={this.pageSize} country='in' category='business'/> </li>
          <li exact path="/entertainment"><NewsShow Mode={dark} key='entertainment'pageSize={this.pageSize} country='in' category='entertainment'/> </li>
          <li exact path="/general"><NewsShow Mode={dark} key='general'pageSize={this.pageSize} country='in' category='general'/> </li>
          <li exact path="/health"><NewsShow Mode={dark} key='health'pageSize={this.pageSize} country='in' category='health'/> </li>
          <li exact path="/science"><NewsShow Mode={dark} key='science'pageSize={this.pageSize} country='in' category='science'/> </li>
          <li exact path="/sports"><NewsShow Mode={dark} key='sports'pageSize={this.pageSize} country='in' category='sports'/> </li>
          <li exact path="/technology"><NewsShow  key='technology'pageSize={this.pageSize} country='in' category='technology'/> </li>
      </Switch>
      </Router> 

      </div>
    )
    }
}

export default App
