import React, { Component } from 'react'
import NewsItems from './NewsItems'

export class NewsShow extends Component {

  articles =[
    {
    
        "source": {
            "id": null,
            "name": "CNBC"
        },
        "author": "Amelia Lucas",
        "title": "Restaurants expect strong sales this summer. Consumers aren't so sure - CNBC",
        "description": "Last year, restaurant sales in May, June and July were tepid as higher gas prices and concerns about the economy weighed on consumers.",
        "url": "https://www.cnbc.com/2023/05/29/restaurants-see-strong-summer-sales-while-consumers-fear-inflation.html",
        "urlToImage": "https://image.cnbcfm.com/api/v1/image/103992007-GettyImages-598846372.jpg?v=1685365201&w=1920&h=1080",
        "publishedAt": "2023-05-29T13:00:01Z",
        "content": "Warmer weather usually boosts restaurant sales, but diners may hold back for the second straight summer as inflation weighs on consumers' minds and wallets.\r\n\"I think operators are still hopeful for … [+4793 chars]"
    },

   
  ]
    constructor(){
        super();
        this.state={
        articles:this.articles,
        loading:false,
      }
    }
    async componentDidMount(){
        let url ="https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=19482a63981f4fe786ff71d0c95930bc&page=2";
        let data =await fetch(url);
        let parsedData =await data.json();
        console.log(parsedData)
        this.setState({articles:parsedData.articles})
    }
    hadlePrevClick= async()=>{
      let url =`https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=19482a63981f4fe786ff71d0c95930bc&page=${this.state.page-1}`
      let data =await fetch(url);
      let parsedData =await data.json();
      console.log(parsedData)
      this.setState({
          articles:parsedData.articles,
          page:this.state-1
       })
  
  }
   hadleNextClick = async()=>{
      let url =`https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=19482a63981f4fe786ff71d0c95930bc&page=${this.state.page+1}`
      let data =await fetch(url);
      let parsedData =await data.json();
      console.log(parsedData)
      this.setState({
          articles:parsedData.articles,
          page:this.state+1
  
       })
      
  
  }
   
    
  render() {
    return (
        
      <div className='container my-3'>
      <h2 className='container text-center' >Top Headline</h2>
     
      <div className='row'>
      {this.state.articles.map((element)=>{
        return <div className='col-md-4' key={element.url} >
               <NewsItems title={element.title} description={element.description} urlToImage={element.urlToImage} url={element.url} />
               </div>
        
            }
      )}
      
<div className="d-flex justify-content-between">
      <button disabled={this.state.page<=1} type="button" className="btn btn-dark" onClick={this.hadlePrevClick} >Previous</button>
      <button disabled={this.state.page>1} type="button" className="btn btn-dark " onClick={this.hadleNextClick} >Next</button>
      </div>
     

      </div>
      </div>
      
    )
  }
}

export default NewsShow
