import React, { Component } from 'react'
import NewsItems from './NewsItems'

export class NewsShow extends Component {

    

    constructor(){
        super();
        this.state={
        articles:this.articles,
        loading:false,
        page:1
      }
    }
    async componentDidMount(){
        let url ="https://newsapi.org/v2/everything?q=tesla&from=2023-04-29&sortBy=publishedAt&apiKey=19482a63981f4fe786ff71d0c95930bc"
        let data =await fetch(url);
        let parseData =await data.json();
        console.log(parseData)
        this.setState({
           articles:parseData.articles})
    }
    hadlePrevClick= async()=>{
      let url =`https://newsapi.org/v2/everything?q=tesla&from=2023-04-29&sortBy=publishedAt&apiKey=c51edf4abfe14356ab1512dc1e5b7854&page=${this.state.page-1}`
      let data =await fetch(url);
      let parseData =await data.json();
      console.log(parseData)
      this.setState({
          articles:parseData.articles,
          page:this.state-1
       })
  
  }
   hadleNextClick = async()=>{
      let url =`https://newsapi.org/v2/everything?q=tesla&from=2023-04-29&sortBy=publishedAt&apiKey=c51edf4abfe14356ab1512dc1e5b7854&page=${this.state.page+1}`
      let data =await fetch(url);
      let parseData =await data.json();
      console.log(parseData)
      this.setState({
          articles:parseData.articles,
          page:this.state+1
  
       })
      
  
  }
    
  render() {
    return (
        
      <div className='container my-3'>
      <h2 className='container text-center' >Top Headline</h2>
     
      <div className='row'>
      {this.state.articles.map((element)=>{
        return <div className='col-md-4' key={element.url} >
               <NewsItems title={element.title} description={element.description} urlToImage={element.urlToImage} url={element.url} />
               </div>
        
            }
      )}
      


      </div>
      </div>
      
    )
  }
}

export default NewsShow
